import React from "react";
import MyBtn from "../buttons/MyBtn";

const Myform = (props) => {
  return (
    <form style={styles.myForm} onSubmit={props.addName}>
      <input
        type="text"
        style={styles.input}
        onChange={props.changeObj}
        placeholder="Name"
      />
      <p>New Item: {props.myInput}</p>
      <MyBtn btnText="Add" />
    </form>
  );
};

export default Myform;

const styles = {
  myForm: {
    backgroundColor: "#ff0000",
    padding: ".5rem",
    width: "50%",
  },
};
