import React from "react";
import MyBtn from "../buttons/MyBtn";

const Myform = (props) => {
  return (
    <form style={styles.myForm} onSubmit={props.addName}>
      <label for="title">Title</label>
      <input
        type="text"
        style={styles.input}
        onChange={props.changeObj}
        placeholder="Name"
      />
      <label for="description">Description</label>
      <input
        type="text"
        style={styles.input}
        onChange={props.changeObj}
        placeholder="Name"
      />
      <p>New Item: {props.myInput}</p>
      <MyBtn btnText="Add" />
    </form>
  );
};

export default Myform;

const styles = {
  myForm: {
    backgroundColor: "#aeaeae",
    padding: "2rem 3rem",
    width: "40%",
    maxWidth: "100%",
  },
  input: {
    width: "100%",
    padding: ".7rem",
  },
};
