import React from "react";
import MyBtn from "../buttons/MyBtn";

const Myform = (props) => {
  return (
    <form style={styles.myForm} onSubmit={props.addName}>
      <input
        type="text"
        style={styles.input}
        onChange={props.changeObj}
        placeholder="Title"
      />
      <input
        type="text"
        style={styles.input}
        onChange={props.changeObj}
        placeholder="Description"
      />
      <p>New Item: {props.myInput}</p>
      <MyBtn btnText="Post" />
    </form>
  );
};

export default Myform;

const styles = {
  myForm: {
    backgroundColor: "#ccc",
    padding: "30px 40px",
    width: "30.5rem",
    maxWidth: "100%",
    marginLeft: "10rem",
    marginTop: "2rem",
    marginBottom: "5rem",
    boxSizing: "border-box",
  },
  input: {
    width: "100%",
    padding: ".7rem",
    marginBottom: ".5rem",
    marginTop: ".5rem",
    outline: "none",
  },
};
