import React from "react";
import Search from "../search/Search";
import { FaRegSun } from "../../../node_modules/react-icons/fa";
import instagram from "../../image/person.jpg";

const Header = (props) => {
  return (
    <header style={styles.header}>
      <h1 style={styles.h1}>{props.pgTitle}</h1>
      <Search />
      <div style={styles.div}>
        <img src={instagram} alt="instagram" style={styles.avatar} />
        <p>John</p>
        <FaRegSun style={styles.setting} />
      </div>
    </header>
  );
};

export default Header;

const styles = {
  header: {
    position: "fixed",
    display: "flex",
    flextDirection: "row",
    backgroundColor: "#ccc",
    justifyContent: "space-between",
    width: "100%",
    height: "5rem",
    alignItems: "center",
  },
  h1: {
    fontSize: "1.5rem",
    color: "#000000",
    marginLeft: "1rem",
  },
  div: {
    marginRight: "7rem",
    display: "flex",
    flextDirection: "column",
    alignItems: "center",
  },
  avatar: {
    width: "1.5rem",
    paddingRight: ".5rem",
  },
  setting: {
    color: "#ff0000",
    paddingLeft: ".5rem",
    size: "2rem",
  },
};
