import React, { Component } from "react";
import "./App.css";
import Header from "./components/header/Header";
import Footer from "./components/footer/Footer";
import MyBtn from "./components/buttons/MyBtn";
import Myform from "./components/myForm/Myform";
import Nav from "./components/nav/Nav";

class App extends Component {
  //create state for binding
  state = {
    myInput: "",
  };

  //Method to takecare of the databinding
  changeObj = (e) => {
    this.setState({ myInput: e.target.value });
  };

  render() {
    return (
      <div style={styles.container}>
        <Header pgTitle="John's App" />
        {/* <Myform changeObj={this.changeObj} myInput={this.state.myInput} /> */}
        <Nav sideNav="Side Nav" />
        <Footer footerText="copyright &copy; 2020 by john" />
      </div>
    );
  }
}

export default App;

const styles = {
  container: {
    display: "flex",
  },
};
