import React, { Component } from "react";
import "./App.css";
import Header from "./components/header/Header";
import Footer from "./components/footer/Footer";
// import MyBtn from "./components/buttons/MyBtn";
import Myform from "./components/myForm/Myform";
import Nav from "./components/nav/Nav";
import RightNav from "./components/nav/RightNav";
import FormPost from "./components/formPost/FormPost";

class App extends Component {
  //create state for binding
  state = {
    myInput: "",
  };

  //Method to takecare of the databinding
  changeObj = (e) => {
    this.setState({ myInput: e.target.value });
  };

  render() {
    return (
      <div style={styles.container}>
        <Header pgTitle="John's App" />
        <Nav />
        <main style={styles.main}>
          <Myform changeObj={this.changeObj} myInput={this.state.myInput} />
          <FormPost />
        </main>
        {/* <RightNav /> */}
        <Footer footerText="copyright &copy; 2020 by john" />
      </div>
    );
  }
}

export default App;

const styles = {
  main: {
    display: "flex",
    flexDirection: "row",
    border: "5px solid red",
    // height: "100vh",
    justifyContent: "space-between",
    backgroundGround: "#ccc",
    marginLeft: "10rem",
    marginRight: "10rem",
  },
};
