import React from "react";
import MyBtn from "../buttons/MyBtn";

const FormPost = (props) => {
  return (
    <article style={styles.article} key={props.id}>
      <img style={styles.img} src={props.avatar} alt={props.alt} />
      <h4>
        {"Title: "}
        {props.title}
      </h4>
      <p>
        {"Description: "}
        {props.description}
      </p>
      <p>{props.newPost}</p>
      <button onClick={props.deletePost}>Delete Post</button>
      <MyBtn onClick={props.deletePost} btnText="Delete Post" />
      <MyBtn onClick={props.editPost} btnText="Edit Post" />
    </article>
  );
};

export default FormPost;

const styles = {
  article: {
    backgroundColor: "#ccc",
    padding: "1rem",
    width: "70%",
    marginLeft: "8rem",

    marginTop: "1rem",
  },

  img: {
    width: "20px",
  },
};
