import React from "react";
import MyBtn from "../buttons/MyBtn";

const FormPost = (props) => {
  return (
    <article style={styles.article} key={props.id}>
      <img style={styles.img} src={props.avatar} alt={props.alt} />
      <h3>{props.title}</h3>
      <p>{props.description}</p>
      <p>{props.newPost}</p>
      <MyBtn btnText="Delete Post" />
      <MyBtn onClick={props.deleteItem} btnText="Edit Post" />
    </article>
  );
};

export default FormPost;

const styles = {
  article: {
    backgroundColor: "#ccc",
    padding: "1rem",
    width: "70%",
    marginLeft: "8rem",

    marginTop: "1rem",
  },

  img: {
    width: "20px",
  },
};
